package com.uacf.identity.internal.model;

import android.content.Context;
import com.uacf.core.util.CollectionUtils;
import com.uacf.core.util.Ln;
import com.uacf.core.util.Strings;
import com.uacf.identity.R;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.Collection;
import java.util.List;

public class IdmConfiguration {
    private static List<String> signatures;

    public IdmConfiguration(Context context) {
        if (signatures == null) {
            signatures = new ArrayList(Arrays.asList(new String[]{"", "", ""}));
            Ln.d("CONTENT: creating signatures", new Object[0]);
            String string = context.getString(R.string.sample_signing_key);
            if (Strings.notEmpty(string)) {
                Ln.d("CONTENT: adding debug", new Object[0]);
                signatures.add(string);
            }
        }
    }

    public List<String> getSignatures() {
        Ln.d("CONTENT: getSignatures, size = %s", Integer.valueOf(CollectionUtils.size((Collection<?>) signatures)));
        return signatures;
    }
}
